package com.overplay.test.common.local.source.delegateimport android.content.SharedPreferencesimport kotlinx.coroutines.flow.Flowinterface LocalSourceDelegate {    fun <T> getSharedPrefFlow(        key: String,        onChanged: suspend (prefs: SharedPreferences, key: String) -> T    ): Flow<T>    fun <T> sharedPrefFlow(        key: String,        onChanged: suspend (prefs: SharedPreferences, key: String) -> T    ): Lazy<Flow<T>>}